import MagisterThing from './magisterThing'
import File from './file'
import { cleanHtmlContent, parseDate, toString } from './util'

/**
 * @extends MagisterThing
 * @private
 */
class ActivityElement extends MagisterThing {
	/**
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, activity, raw) {
		super(magister)

		/**
		 * @type String
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type String
		 * @readonly
		 */
		this.title = raw.Titel
		/**
		 * @type String
		 * @readonly
		 */
		this.description = cleanHtmlContent(raw.Details)

		/**
		 * @type Date
		 * @readonly
		 */
		this.signinStart = parseDate(raw.StartInschrijfdatum)
		/**
		 * @type Date
		 * @readonly
		 */
		this.signinEnd = parseDate(raw.EindeInschrijfdatum)

		/**
		 * @type Number
		 * @readonly
		 */
		this.minParticipants = raw.MinAantalDeelnemers
		/**
		 * @type Number
		 * @readonly
		 */
		this.maxParticipants = raw.MaxAantalDeelnemers
		/**
		 * @type Number
		 * @readonly
		 */
		this.placesLeft = raw.AantalPlaatsenBeschikbaar

		/**
		 * @type Boolean
		 * @readonly
		 */
		this.signedup = raw.IsIngeschreven
		/**
		 * @type Boolean
		 * @readonly
		 */
		this.ableToSignup = raw.IsOpInTeSchrijven
		/**
		 * @type Boolean
		 * @readonly
		 */
		this.mandatorilySignedup = raw.IsVerplichtIngeschreven

		/**
		 * @type File[]
		 * @readonly
		 */
		this.attachments = (raw.Bijlagen || []).map(a => new File(this._magister, undefined, a))

		// REVIEW: do we want a reference to the parent activity?
		// this._activity = activity
		/**
		 * @type String
		 * @readonly
		 */
		this.activityId = toString(activity.id)
		/**
		 * @type String
		 * @private
		 * @readonly
		 */
		this._url = `${activity._url}/onderdelen/${this.id}`
	}

	/**
	 * @param {Boolean} val
	 * @return {ActivityElement}
	 */
	signup(val) {
		// TODO: handle errors, I have no idea how an error looks like, though.

		if (!this.ableToSignup || this.signedup === val) {
			return
		}

		return Promise.resolve().then(() => {
			const url = `${this._url}/inschrijvingen`

			if (val) {
				const payload = {
					persoonId: this._magister.profileInfo.id,
					activiteitId: Number.parseInt(this.activityId, 10),
					onderdeelId: Number.parseInt(this.id, 10),
				}

				return this._magister._privileges.needs('activiteiten', 'update')
				.then(() => this._magister.http.post(url, payload))
			} else {
				return this._magister._privileges.needs('activiteiten', 'delete')
				.then(() => this._magister.http.delete(url))
			}
		}).then(() => {
			this.signedup = val
			return this
		})
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		return {
			Id: Number.parseInt(this.id, 10),
			Titel: this.title,
			Details: this.description,

			StartInschrijfdatum: this.signinStart.toJSON(),
			EindeInschrijfdatum: this.signinEnd.toJSON(),

			MinAantalDeelnemers: this.minParticipants,
			MaxAantalDeelnemers: this.maxParticipants,
			AantalPlaatsenBeschikbaar: this.placesLeft,

			IsIngeschreven: this.signedup,
			IsOpInTeSchrijven: this.ableToSignup,
			IsVerplichtIngeschreven: this.mandatorilySignedup,

			activiteitId: Number.parseInt(this.activityId, 10),
		}
	}
}

export default ActivityElement

/**
 * @extends Error
 */
class AuthError extends Error {
	/**
	 * @param {String} error
	 */
	constructor(message) {
		super(message)
	}

	/**
	 * @override
	 * @return {String}
	 */
	toString() {
		return 'AuthError: ' + this.message
	}
}

export default AuthError

import _ from 'lodash'
import MagisterThing from './magisterThing'
import Class from './class'
import Grade from './grade'
import { parseDate, toString } from './util'

/**
 * @extends MagisterThing
 * @private
 */
class Course extends MagisterThing {
	/**
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type String
		 * @readonly
		 */
		this.id = toString(raw.Id)

		/**
		 * @type Date
		 * @readonly
		 */
		this.start = parseDate(raw.Start)
		/**
		 * @type Date
		 * @readonly
		 */
		this.end = parseDate(raw.Einde)

		/**
		 * The school year of this course, e.g: '1617'
		 * @type String
		 * @readonly
		 */
		this.schoolPeriod = raw.Lesperiode

		/**
		 * Basic type information of this course, e.g: { description: "VWO 6", id: 420 }
		 * @type Object
		 * @readonly
		 */
		this.type = {
			id: raw.Studie.Id,
			description: raw.Studie.Omschrijving,
		}

		/**
		 * The group of this course, e.g: { description: "Klas 6v3", id: 420, locationId: 0 }
		 * @type Object
		 * @readonly
		 */
		this.group = {
			id: raw.Groep.Id,
			description: (function () {
				const group = raw.Groep.Omschrijving
				if (group != null) {
					return group.split(' ').find(w => /\d/.test(w)) || group
				}
			})(),
			locationId: raw.Groep.LocatieId,
		}

		/**
		 * @type String[]
		 * @readonly
		 */
		this.curricula = [ raw.Profiel ]
		if (raw.Profiel2 != null) {
			this.curricula.push(raw.Profiel2)
		}
	}

	/**
	 * @type Boolean
	 * @readonly
	 */
	get current() {
		const now = new Date()
		return this.start <= now && now <= this.end
	}

	/**
	 * @return {Promise<Class[]>}
	 */
	classes() {
		const url = `${this._magister._personUrl}/aanmeldingen/${this.id}/cijfers`
		return this._magister.http.get(url)
		.then(res => res.json())
		.then(res => res.Items.map(c => new Class(this._magister, c)))
	}

	/**
	 * @param {Object} [options={}]
	 * 	@param {Boolean} [options.fillGrades=true]
	 * @return {Promise<Grade[]>}
	 */
	grades({ fillGrades = true } = {}) {
		const urlPrefix = `${this._magister._personUrl}/aanmeldingen/${this.id}/cijfers`
		const url = `${urlPrefix}/cijferoverzichtvooraanmelding?actievePerioden=false&alleenBerekendeKolommen=false&alleenPTAKolommen=false`

		return this._magister._privileges.needs('cijfers', 'read')
		.then(() => this._magister.http.get(url))
		.then(res => res.json())
		.then(res => res.Items)
		.then(grades => {
			grades = _.reject(grades, raw => raw.CijferId === 0)

			const promises = grades.map(raw => {
				const grade = new Grade(this._magister, raw)
				grade._fillUrl = `${urlPrefix}/extracijferkolominfo/${_.get(raw, 'CijferKolom.Id')}`
				return Promise.resolve(fillGrades ? grade.fill() : grade)
			})

			return Promise.all(promises)
		})
		.then(r => _.sortBy(r, 'dateFilledIn'))
	}
}

export default Course

import url from 'url'
import _ from 'lodash'
import MagisterThing from './magisterThing'
import Person from './person'
import { parseDate, toString } from './util'

/**
 * @extends MagisterThing
 * @private
 */
class File extends MagisterThing {
	/**
	 * @param {Magister} magister
	 * @param {FileFolder} fileFolder
	 * @param {Object} raw
	 */
	constructor(magister, fileFolder, raw) {
		super(magister)

		/**
		 * @type String
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type Number
		 * @readonly
		 */
		this.type = raw.BronSoort // REVIEW: string?
		/**
		 * @type String
		 * @readonly
		 */
		this.name = raw.Naam
		/**
		 * @type String
		 * @readonly
		 */
		this.uri = raw.Uri
		/**
		 * @type Number
		 * @readonly
		 */
		this.size = raw.Grootte
		// REVIEW
		/**
		 * @type Number
		 * @readonly
		 */
		this.rights = raw.Privilege
		/**
		 * @type String
		 * @readonly
		 */
		this.mime = raw.ContentType || 'application/octet-stream'

		/**
		 * @type Date
		 * @readonly
		 */
		this.changedDate = parseDate(raw.GewijzigdOp)
		/**
		 * @type Date
		 * @readonly
		 */
		this.creationDate = parseDate(raw.GemaaktOp || raw.Datum)

		/**
		 * @type Person
		 * @readonly
		 */
		this.addedBy = new Person(magister, { Naam: raw.GeplaatstDoor })

		/**
		 * @type String
		 * @readonly
		 */
		this.fileBlobId = toString(raw.FileBlobId)
		/**
		 * @type FileFolder
		 * @readonly
		 */
		this.fileFolder = fileFolder
		/**
		 * @type String
		 * @readonly
		 */
		this.uniqueId = raw.UniqueId
		/**
		 * @type String
		 * @readonly
		 */
		this.referenceId = toString(raw.Referentie)

		const selfUrl = _.find(raw.Links, { Rel: 'Self' })
		const contentUrl = _.find(raw.Links, { Rel: 'Contents' })
		const getUrl = link => !link ? null : url.resolve(magister.school.url, link.Href)

		/**
		 * @type String|null
		 * @readonly
		 * @private
		 */
		this._selfUrl = getUrl(selfUrl)
		/**
		 * @type String
		 * @readonly
		 * @private
		 */
		this._downloadUrl = getUrl(contentUrl || selfUrl)
	}

	/**
	 * Opens a stream to the current file
	 * @return {Promise<Stream>}
	 */
	download() {
		return this._magister._privileges.needs('bronnen', 'read')
		.then(() => this._magister.http.get(this._downloadUrl))
		.then(res => res.body)
	}

	/**
	 * Removes the current file permanently
	 * @return {Promise<undefined>}
	 */
	remove() {
		return this._magister._privileges.needs('bronnen', 'delete')
		.then(() => this._magister.http.delete(this._selfUrl))
		.then(() => undefined) // throw away the useless result from magister. (current object)
	}

	/**
	 * Update the server to reflect the changes made on the properties of this
	 * File instance.
	 * @return {Promise<undefined>}
	 */
	saveChanges() {
		return this._magister._privileges.needs('bronnen', 'update')
		.then(() => this._magister.http.put(this._selfUrl, this._toMagister()))
		.then(() => undefined)
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		const toNumberSafe = val => val == null ? val : parseInt(val, 10)

		return {
			Id: parseInt(this.id, 10),
			BronSoort: this.type,
			Naam: this.name,
			Uri: this.uri,
			Grootte: this.size,
			Privilege: this.rights,
			ContentType: this.mime,
			FileBlobId: toNumberSafe(this.fileBlobId),
			ParentId: this.fileFolder.id,
			UniqueId: this.uniqueId,
			Referentie: toNumberSafe(this.referenceId),
		}
	}
}

export default File

import MagisterThing from './magisterThing'
import File from './file'
import { toString } from './util'

/**
 * @extends MagisterThing
 * @private
 */
class FileFolder extends MagisterThing {
	/**
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type String
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type String
		 * @readonly
		 */
		this.name = raw.Naam
		/**
		 * @type Number
		 * @readonly
		 */
		this.rights = raw.Privilege
		/**
		 * @type String
		 * @readonly
		 */
		this.parentId = toString(raw.ParentId)
	}

	/**
	 * @param {Boolean} [fillPersons=false]
	 * @return {Promise<File[]>}
	 */
	files(fillPersons = false) {
		const url = `${this._magister._personUrl}/bronnen?parentId=${this.id}`
		return this._magister._privileges.needs('bronnen', 'read')
		.then(() => this._magister.http.get(url))
		.then(res => res.json())
		.then(res => {
			const promises = res.Items.map(f => {
				const file = new File(this._magister, this, f)
				return fillPersons ?
					file.addedBy.getFilled().then(() => file) :
					Promise.resolve(file)
			})
			return Promise.all(promises)
		})
	}
}

export default FileFolder

'use strict'

import _ from 'lodash'
import fetch from 'node-fetch'
import url from 'url'

import AbsenceInfo from './absenceInfo'
import Activity from './activity'
import ActivityElement from './activityElement'
import Appointment from './appointment'
import Assignment from './assignment'
import AssignmentVersion from './assignmentVersion'
import AuthError from './authError'
import Class from './class'
import Course from './course'
import File from './file'
import FileFolder from './fileFolder'
import Grade from './grade'
import GradePeriod from './gradePeriod'
import GradeType from './gradeType'
import Http from './http'
import Message from './message'
import MessageFolder from './messageFolder'
import Person from './person'
import Privileges from './privileges'
import ProfileInfo from './profileInfo'
import School from './school'
import SchoolUtility from './schoolUtility'
import VersionInfo from './versionInfo'
import * as util from './util'

import AddressInfo from './addressInfo'
import ProfileSettings from './profileSettings'

// TODO: add nice warnings when trying to do stuff while not logged in yet

/**
 * Class to communicate with Magister.
 * @private
 */
class Magister {
	/**
	 * @param {Object} options
	 * @param {School} school
	 * @param {Http} http
	 */
	constructor(options, school, http) {
		const info = url.parse(school.url)
		if (!/^[a-z]+\.magister\.net$/.test(info.host)) {
			throw new Error('`school.url` is not a correct magister url')
		}
		school.url = `https://${info.host}`


		/**
		 * @type Object
		 * @readonly
		 * @private
		 */
		this._options = options
		/**
		 * @type School
		 * @readonly
		 */
		this.school = _.extend(new School({}), school)
		/**
		 * @type Http
		 * @readonly
		 */
		this.http = http
		/**
		 * @type ProfileInfo
		 * @readonly
		 */
		this.profileInfo = null
	}

	/**
	 * @return {Promise<Activity[]>}
	 */
	activities() {
		return this._privileges.needs('activiteiten', 'read')
		.then(() => this.http.get(`${this._personUrl}/activiteiten`))
		.then(res => res.json())
		.then(res => res.Items.map(a => new Activity(this, a)))
	}

	/**
	 * @param {Date} from Time is ignored.
	 * @param {Date} [to=from] Time is ignored
	 * @param {Object} [options={}]
	 * 	@param {Boolean} [options.fillPersons=false]
	 * 	@param {Boolean} [options.fetchAbsences=true]
	 * 	@param {Boolean} [options.ignoreAbsenceErrors=true]
	 * @return {Promise<Appointment[]>}
	 */
	appointments() {
		// extract options
		const {
			fillPersons = false,
			fetchAbsences = true,
			ignoreAbsenceErrors = true,
		} = _.find(arguments, _.isPlainObject) || {}

		// extract dates
		const dates = _(arguments).filter(_.isDate).sortBy().value()
		const from = dates[0]
		const to = dates[1] || dates[0]

		const fromUrl = util.urlDateConvert(from)
		const toUrl = util.urlDateConvert(to)

		// fetch appointments
		const appointmentsUrl = `${this._personUrl}/afspraken?van=${fromUrl}&tot=${toUrl}`
		const appointmentsPromise = this._privileges.needs('afspraken', 'read')
		.then(() => this.http.get(appointmentsUrl))
		.then(res => res.json())
		.then(res => res.Items.map(a => new Appointment(this, a)))
		.then(appointments => {
			if (!fillPersons) {
				return appointments
			}

			const promises = appointments.map(a => {
				return Promise.all(a.teachers.map(t => t.getFilled('teacher')))
				.then(teachers => a.teachers = teachers)
				.then(() => a)
			})
			return Promise.all(promises)
		})

		// fetch absences
		let absencesPromise = Promise.resolve([])
		if (fetchAbsences) {
			const absencesUrl = `${this._personUrl}/absenties?van=${fromUrl}&tot=${toUrl}`
			absencesPromise = this._privileges.needs('Absenties', 'read')
			.then(() => this.http.get(absencesUrl))
			.then(res => res.json())
			.then(res => res.Items.map(a => new AbsenceInfo(this, a)))

			if (ignoreAbsenceErrors) {
				absencesPromise = absencesPromise.catch(() => [])
			}
		}

		return Promise.all([ appointmentsPromise, absencesPromise ])
		.then(([ appointments, absences ]) => {
			for (const a of appointments) {
				a.absenceInfo = absences.find(i => i.appointment.id === a.id)
			}

			return appointments
		})
		.then(appointments => _.sortBy(appointments, 'start'))
	}

	/**
	 * @param {Object} [options={}]
	 * 	@param {Number} [options.count=50]
	 * 	@param {Number} [options.skip=0]]
	 * 	@param {Boolean} [options.fillPersons=false]
	 * @return {Promise<Assignment[]>}
	 */
	assignments({ count = 50, skip = 0, fillPersons = false } = {}) {
		const url = `${this._personUrl}/opdrachten?top=${count}&skip=${skip}&status=alle`

		return this._privileges.needs('eloopdracht', 'read')
		.then(() => this.http.get(url))
		.then(res => res.json())
		.then(res => res.Items.map(i => i.Id))
		.then(ids => {
			const promises = ids.map(id => {
				return this.http.get(`${this._personUrl}/opdrachten/${id}`)
				.then(res => res.json())
			})
			return Promise.all(promises)
		})
		.then(items => {
			const promises = items.map(item => {
				const assignment = new Assignment(this, item)
				if (!fillPersons) {
					return assignment
				}

				return Promise.all(assignment.teachers.map(p => p.getFilled('teacher')))
				.then(teachers => assignment.teachers = teachers)
				.then(() => assignment)
			})
			return Promise.all(promises)
		})
	}

	/**
	 * @return {Promise<Magister[]>}
	 */
	children() {
		if (this.profileInfo.isChild) {
			return Promise.reject(new Error('User is not a parent'))
		}

		return this.http.get(`${this._personUrl}/kinderen`)
		.then(res => res.json())
		.then(res => res.Items)
		.then(items => items.map(raw => {
			const m = Object.create(this)

			m.school = this.school
			m.http = this.http

			m._personUrl = `${this.school.url}/api/personen/${raw.Id}`
			m._pupilUrl = `${this.school.url}/api/leerlingen/${raw.Id}`
			m.profileInfo = new ProfileInfo(this, raw)

			return m
		}))
	}

	/**
	 * @return {Promise<Course>}
	 */
	courses() {
		return this._privileges.needs('aanmeldingen', 'read')
		.then(() => this.http.get(`${this._personUrl}/aanmeldingen`))
		.then(res => res.json())
		.then(res => res.Items.map(c => new Course(this, c)))
		.then(items => _.sortBy(items, 'start'))
	}

	/**
	 * @param {Object} options
	 * 	@param {String} options.description The description of the appointment.
	 * 	@param {Date} options.start The start of the appointment, time is
	 * 	ignored when `options.fullDay` is set to true.
	 * 	@param {Date} options.end The end of the appointment, this is ignored
	 * 	when `options.fullDay` is set to true.
	 * 	@param {Boolean} [options.fullDay=false] When this is true,
	 * 	`options.end` is ignored and only `options.start` is used to set the
	 * 	begin and the end for the appointment.
	 * 	@param {String} [options.location] The location (classroom for example)
	 * 	for the appointment.
	 * 	@param {String} [options.content] Some arbitrary string you want to
	 * 	save.
	 * 	@param {Number} [options.type=1] The type of the appointment: 1 for
	 * 	personal or 16 for planning
	 * @return {Promise}
	 */
	createAppointment(options) {
		const required = [ 'description', 'start', 'end' ]
		for (const key of required) {
			if (options[key] == null) {
				const err = new Error(`Not all required fields for \`options\` are given, required are: [ ${required.join(', ')} ]`)
				return Promise.reject(err)
			}
		}

		if (options.fullDay) {
			options.start = util.date(options.start)
			options.end = new Date(options.start.getTime()) + 1000*60*60*24
		}

		const payload = {
			Omschrijving: options.description,
			Start: options.start.toJSON(),
			Einde: options.end.toJSON(),

			Lokatie: _.trim(options.location),
			Inhoud: (function () {
				const content = _.trim(options.content)
				return content.length > 0 ? _.escape(content) : null
			})(),
			Type: options.type || 1,
			DuurtHeleDag: options.fullDay || false,

			// Static non-configurable stuff.
			InfoType: 0,
			WeergaveType: 1,
			Status: 2,
			HeeftBijlagen: false,
			Bijlagen: null,
			LesuurVan: null,
			LesuurTotMet: null,
			Aantekening: null,
			Afgerond: false,
			Vakken: null,
			Docenten: null,
			Links: null,
			Id: 0,
			Lokalen: null,
			Groepen: null,
			OpdrachtId: 0,
		}

		return this._privileges.needs('afspraken', 'create')
		.then(() => this.http.post(`${this._personUrl}/afspraken`, payload))
	}

	/**
	 * @return {Promise<FileFolder[]>}
	 */
	fileFolders() {
		return this._privileges.needs('bronnen', 'read')
		.then(() => this.http.get(`${this._personUrl}/bronnen?soort=0`))
		.then(res => res.json())
		.then(res => res.Items.map(f => new FileFolder(this, f)))
	}

	/**
	 * @return {Promise<MessageFolder[]>}
	 */
	messageFolders() {
		return this._privileges.needs('berichten', 'read')
		.then(() => this.http.get(`${this._personUrl}/berichten/mappen`))
		.then(res => res.json())
		.then(res => res.Items.map(m => new MessageFolder(this, m)))
	}

	/**
	 * @param {String} query
	 * @param {String} [type]
	 * @return {Promise<Person[]>}
	 */
	persons(query, type) {
		query = query != null ? query.trim() : ''

		if (query.length < 3) {
