import moment from 'moment'

/**
 * Tries to parse `val` to a date, if it fails this function will return
 * `undefined` instead of an invalid date.
 *
 * @param {any} val
 * @return {Date|undefined}
 */
export function parseDate (val) {
	const n = Date.parse(val)
	if (!isNaN(n)) {
		return new Date(n)
	}
}

/**
 * @param {Date} date
 * @return {Date}
 */
export function date (date) {
	return new Date(date.getFullYear(), date.getMonth(), date.getDate())
}

/**
 * @param {Date} date
 * @return {String}
 */
export function urlDateConvert (date) {
	return moment(date).format('Y-MM-DD')
}

import _ from 'lodash'

/**
 * Strips HTML tags and entities from the given `str`
 * If `str` is `undefined` or `null` an empty string will be returned.
 *
 * @param {String} [str]
 * @return {String}
 */
export function cleanHtmlContent (str) {
	if (str == null) {
		return ''
	}

	return _.unescape(str)
	.replace(/<br\s*\/?>/g, '\n')
	.replace(/<\/\s*p\s*>/g, '\n')
	.replace(/&nbsp;/g, ' ')
	.replace(/(<[^>]*>)|(&#x200b;)/g, '')
	.replace(/\r?\n/g, '\n')
	.trim()
}

/**
 * Returns the value as a string, or the value itself if it's `undefined` or
 * `null`.
 * @param {any} val
 * @return {String|undefined|null}
 */
export function toString(val) {
	if (val == null) {
		return val
	} else {
		return val.toString()
	}
}
